mapper.update:
UPDATE user
SET u_nick=#{u_nick}, u_pw=#{u_pw}, birth=#{birth}, gender=#{gender}
WHERE u_id=#{u_id};

mapper.select:
SELECT    u_nick, u_id, u_pw, birth, gender
FROM    user
WHERE    u_id = #{id};

SELECT count(*)
FROM user
WHERE u_id = #{param1} AND u_pw = #{param2}

mapper.update:
UPDATE user
SET u_flag=0
WHERE u_id = #{id};
