mapper.select:
SELECT count(*)
FROM course
WHERE writer = #{u_nick} AND rest_id = #{r_id} AND c_id = #{c_id};

mapper.update:
		UPDATE course INNER JOIN user ON (course.writer = user.u_nick)
      	SET course.flag = #{flag}
      	WHERE  course.rest_id = #{r_id}  
			AND course.cafe_id= #{c_id} 
			AND user.u_id = #{u_id}

