mapper.select:

SELECT *
FROM user
WHERE u_id = #{u_id} AND u_flag = "1";

SELECT u_id 
FROM user
WHERE u_email = #{u_email};

SELECT count(*)
FROM user
WHERE u_id = #{u_id} AND u_email = #{u_email} ;

mapper.update:
UPDATE user
SET u_pw=#{u_pw}
WHERE u_id = #{u_id};

mapper.select:
SELECT count(*)
FROM emailauth
WHERE email=#{email} AND authvalue=#{auth};

SELECT u_id
FROM user
WHERE u_email = #{u_email};
